import { NextPage } from 'next';
import Layout from 'components/layout/main';
import { Box, makeStyles, Typography, useMediaQuery, useTheme } from '@material-ui/core';
import TopImg from 'assets/about/homeTop.png';
import { LayoutGrade } from 'recoilStates/layout';
import AspectRatioBox from 'components/shape/aspectRatioBox';

const useStyles = makeStyles(_theme => ({
  wrapper: {
    backgroundRepeat: 'no-repeat',
    backgroundPosition: "center",
    backgroundSize: 'cover',
    width:"100%"
  },  
}))
const Brand: NextPage = () => {
  const theme = useTheme();
  const mobileFlag = useMediaQuery(theme.breakpoints.down(LayoutGrade.MD));
  const classes = useStyles();

  return (
    <Layout>
      <Box padding={2} paddingTop={5} paddingBottom={5} display="flex">
        <Box width={"50%"}>
          <Typography
            variant="subtitle2"
            component={"span"}
            style={{ borderBottom: `2px solid ${theme.palette.primary.main}` }}
          >
            홈클리닝 서비스 안내
          </Typography>
          <Box paddingTop={3}>
            <Typography variant="body2">가족의 행복한 미래를 꿈꾸는</Typography>
            <Typography variant="body2">
              깨끗한 주거 환경을 만들어드립니다!
            </Typography>
            <br />
            <Typography variant="body2">
              오래된 먼지, 곰팡이, 부착물 등 집 안에 있는
            </Typography>
            <Typography variant="body2">
              각종 유해한 오염 물질들을 말끔하게
            </Typography>
            <Typography variant="body2">제거하는 청소 서비스입니다.</Typography>
            <br />
            <br />
            <Typography color="primary">새집증후군, 집 먼지 진드기</Typography>
            <Typography variant="body2">고생하시는 분들이 많습니다.</Typography>
            <Typography variant="body2">
              친환경적인 제품으로 쾌적하고 깨끗한 환경에
            </Typography>
            <Typography variant="body2">
              입주할 수 있도록 도와드리겠습니다.
            </Typography>
          </Box>
          <AspectRatioBox width="50%" aspectRatio={1}>
            <Box
              height="100%"
              className={classes.wrapper}
              style={{ backgroundImage: `url(${TopImg})` }}
            />
          </AspectRatioBox>
        </Box>
      </Box>
    </Layout>
  );
};

export default Brand;
