import { NextPage } from "next";
import Layout from "components/layout/main";
import {
  Box,
  Grid,
  makeStyles,
  Typography,
  useMediaQuery,
  useTheme,
} from "@material-ui/core";
import TopImg from "assets/about/virusTop.png";
import HomeImg1 from "assets/about/virus_img1.png";
import HomeImg2 from "assets/about/virus_img2.png";
import HomeImg3 from "assets/about/virus_img3.png";
import Chart from "assets/about/Chart.png";
import { LayoutGrade } from "recoilStates/layout";
import AspectRatioBox from "components/shape/aspectRatioBox";

const useStyles = makeStyles((_theme) => ({
  wrapper: {
    backgroundRepeat: "no-repeat",
    backgroundPosition: "center",
    backgroundSize: "cover",
    width: "100%",
  },
}));
const contentData = [
  {
    title: "코로나19",
    img: HomeImg1,
    content: "코로나바이러스(Coronavirus; CoV)는 사람을 포함한 동물에<br/>광범위한 호흡계 및 소화계 감염을 일으키는 바이러스로<br/>심각한 폐 질환을 일으키고 발열, 기침, 호흡 곤란 설사와 같이 경증에서<br/>중증의 증상을 보이고 심각한 경우 사망할 수 있습니다.",
  },
  {
    title: "독감(인플루엔자)",
    img: HomeImg2,
    content1: "■ 문틈, 창문 틀, 유리, 방충망 먼지제거",
    content2: "■ 벽, 천장 부위 먼지 및 이물질 제거",
    content3: "■ 몰딩 부분 먼지 및 오염 제거",
    content4: "■ 내부 유리창 오염물 제거",
  },
  {
    title: "감기",
    img: HomeImg3,
    content1: "■ 책상, 의자, 테이블 등 사무용 가구 청소",
    content2: "■ 전화기, PC, 복합기 등 OA기기 청소",
    content3: "■ 사무공간 주변 쓰레기 및 오염제거",
    content4: "■ 파티션, 쇼파 클리닝(비용추가)",
  },
  
];
const Brand: NextPage = () => {
  const theme = useTheme();
  const mobileFlag = useMediaQuery(theme.breakpoints.down(LayoutGrade.MD));
  const classes = useStyles();
  const contentMap = contentData.map((obj, i) => {
    return (
      <Box display={'flex'} padding={1} justifyContent='space-between'>
            <Box width={'10%'}>
              <AspectRatioBox width="100%" aspectRatio={1}>
                <Box
                  width="100%"
                  height="100%"
                  className={classes.wrapper}
                  style={{ backgroundImage: `url(${obj.img})` }}
                />
              </AspectRatioBox>
            </Box>
            <Box width={'90%'}>
              <Box paddingX={5}>
              <Typography>코로나 19</Typography>
              <Typography variant="body2">
                {obj.content}
              </Typography>
              </Box>
            </Box>
          </Box>
    );
  });
  return (
    <Layout>
      <Box
        padding={2}
        paddingTop={10}
        paddingBottom={10}
        display="flex"
        justifyContent="space-between"
      >
        <Box width={"70%"}>
          <Typography
            variant="subtitle1"
            component={"span"}
            style={{ borderBottom: `2px solid ${theme.palette.primary.main}` }}
          >
            방역소〮독 서비스 안내
          </Typography>
          <Box paddingTop={3}>
            <Typography variant="body1">바이러스와 세균을 제거하여 </Typography>
            <Typography variant="body1">
              안전한 시설을 만들어드립니다!
            </Typography>
            <br />
            <Typography variant="body1">
              다양한 환경에서 발생할 수 있는
            </Typography>
            <Typography variant="body1">
              각종 세균과 바이러스의 발생을 효과적으로
            </Typography>
            <Typography variant="body1">
              예방할 수 있도록 현장 전체를 소독하는 서비스입니다.
            </Typography>
            <br />
            <br />
            <Typography color="primary" variant="body1">
              전염성 질환으로 위생과 방역에 대한 중요성이 높아지고 있어
            </Typography>
            <Typography variant="body1">
              전문적인 방역, 소독을 진행하여 다양한 업종과 환경에 발생되는
            </Typography>
            <Typography variant="body1">
              바이러스와 세균을 차단하여 안전한 시설을 만들어드립니다.
            </Typography>
          </Box>
        </Box>
        <AspectRatioBox width="40%" aspectRatio={1}>
          <img height={"100%"} src={TopImg} />
        </AspectRatioBox>
      </Box>
      <Box padding={2} paddingBottom={10}>
        <Box textAlign={"center"}>
          <Typography
            variant="subtitle1"
            component={"span"}
            style={{ borderBottom: `2px solid ${theme.palette.primary.main}` }}
          >
            주요 발생 &nbsp;
          </Typography>
          <Typography
            color="primary"
            variant="subtitle1"
            component={"span"}
            style={{ borderBottom: `2px solid ${theme.palette.primary.main}` }}
          >
            바이러스
          </Typography>
        </Box>
        <Box paddingTop={5}>
          
        </Box>
      </Box>
      <Box padding={2} paddingBottom={10}>
        <Box textAlign={"center"}>
          <Typography
            variant="subtitle1"
            component={"span"}
            style={{ borderBottom: `2px solid ${theme.palette.primary.main}` }}
          >
            방역소〮독 서비스 대상
          </Typography>
        </Box>
        <img width={"100%"} src={Chart} />
      </Box>
    </Layout>
  );
};

export default Brand;
