import { useEffect } from "react";
import { useRecoilValue } from 'recoil';
import { windowLayoutSelector, getBasicLayout } from 'recoilStates/layout';

const useResizeEvent = (elementRef, effectCallback) => {
  const windowLayout = useRecoilValue(windowLayoutSelector);
  useEffect(() => {
    const ref = elementRef.current;

    if (ref) {
      const endEffect = effectCallback(getBasicLayout(ref));
      return () => {
        if (endEffect) endEffect(getBasicLayout(ref));
      };
    }
  }, [windowLayout, elementRef]);
};

export default useResizeEvent;
