import { NextPage } from "next";
import Layout from "components/layout/main";
import {
  Box,
  Grid,
  makeStyles,
  Typography,
  useMediaQuery,
  useTheme,
} from "@material-ui/core";
import TopImg from "assets/about/virusTop.png";
import HomeImg1 from "assets/about/office_img_1.png";
import HomeImg2 from "assets/about/office_img_2.png";
import HomeImg3 from "assets/about/office_img_3.png";
import HomeImg4 from "assets/about/office_img_4.png";
import { LayoutGrade } from "recoilStates/layout";
import AspectRatioBox from "components/shape/aspectRatioBox";

const useStyles = makeStyles((_theme) => ({
  wrapper: {
    backgroundRepeat: "no-repeat",
    backgroundPosition: "center",
    backgroundSize: "cover",
    width: "100%",
  },
}));
const contentData = [
  {
    title: "바닥",
    img: HomeImg1,
    content1: "■ 먼지 및 오염제거",
    content2: "■ 바닥왁스코팅",
    content3: "■ 카페트크리닝",
    content4: "■ 석재관리",
  },
  {
    title: "천장, 창문",
    img: HomeImg2,
    content1: "■ 문틈, 창문 틀, 유리, 방충망 먼지제거",
    content2: "■ 벽, 천장 부위 먼지 및 이물질 제거",
    content3: "■ 몰딩 부분 먼지 및 오염 제거",
    content4: "■ 내부 유리창 오염물 제거",
  },
  {
    title: "회의실, 업무공간",
    img: HomeImg3,
    content1: "■ 책상, 의자, 테이블 등 사무용 가구 청소",
    content2: "■ 전화기, PC, 복합기 등 OA기기 청소",
    content3: "■ 사무공간 주변 쓰레기 및 오염제거",
    content4: "■ 파티션, 쇼파 클리닝(비용추가)",
  },
  {
    title: "기타시설",
    img: HomeImg4,
    content1: "■ 화장실 및 탕비실 청소",
    content2: "■ 기타 주변 공간 청소",
    content3: "■ 엘리베이터 청소",
    content4: "■ 냉, 난방기 청소",
  },
];
const Brand: NextPage = () => {
  const theme = useTheme();
  const mobileFlag = useMediaQuery(theme.breakpoints.down(LayoutGrade.MD));
  const classes = useStyles();
  const contentMap = contentData.map((obj, i) => {
    return (
      <Grid item xs={6} style={{ paddingTop: "50px" }}>
        <Typography variant="subtitle2" style={{paddingBottom:'15px'}}>{obj.title}</Typography>
        <AspectRatioBox width="90%" aspectRatio={0.7}>
          <Box
            width="100%"
            height="100%"
            className={classes.wrapper}
            style={{ backgroundImage: `url(${obj.img})` }}
          />
        </AspectRatioBox>
        <Box paddingTop={2}>
          <Typography style={{ lineHeight:'1.8'}} variant="body2">{obj.content1}</Typography>
          <Typography style={{ lineHeight:'1.8'}} variant="body2">{obj.content2}</Typography>
          <Typography style={{ lineHeight:'1.8'}} variant="body2">{obj.content3}</Typography>
          <Typography style={{ lineHeight:'1.8'}} variant="body2">{obj.content4}</Typography>
        </Box>
      </Grid>
    );
  });
  return (
    <Layout>
      <Box
        padding={2}
        paddingTop={10}
        paddingBottom={10}
        display="flex"
        justifyContent="space-between"
      >
        <Box width={"70%"}>
          <Typography
            variant="subtitle1"
            component={"span"}
            style={{ borderBottom: `2px solid ${theme.palette.primary.main}` }}
          >
            방역소〮독 서비스 안내
          </Typography>
          <Box paddingTop={3}>
            <Typography variant="body1">바이러스와 세균을 제거하여 </Typography>
            <Typography variant="body1">
            안전한 시설을 만들어드립니다!
            </Typography>
            <br />
            <Typography variant="body1">
            다양한 환경에서 발생할 수 있는 
            </Typography>
            <Typography variant="body1">
            각종 세균과 바이러스의 발생을 효과적으로
            </Typography>
            <Typography variant="body1">예방할 수 있도록 현장 전체를 소독하는 서비스입니다.</Typography>
            <br />
            <br />
            <Typography color="primary" variant="body1">전염성 질환으로 위생과 방역에 대한 중요성이 높아지고 있어</Typography>
            <Typography variant="body1">전문적인 방역, 소독을 진행하여 다양한 업종과 환경에 발생되는</Typography>
            <Typography variant="body1">바이러스와 세균을 차단하여 안전한 시설을 만들어드립니다.</Typography>
          </Box>
        </Box>
        <AspectRatioBox width="30%" aspectRatio={1}>
          <Box
            width="100%"
            height="100%"
            className={classes.wrapper}
            style={{ backgroundImage: `url(${TopImg})` }}
          />
        </AspectRatioBox>
      </Box>
      <Box padding={2} paddingBottom={10}>
          <Typography
            variant="subtitle1"
            component={"span"}
            style={{ borderBottom: `2px solid ${theme.palette.primary.main}` }}
          >
            오피스크리닝 서비스내용
          </Typography>
        <Box paddingTop={2}>
          <Grid container spacing={8}>
            {contentMap}
          </Grid>
        </Box>
      </Box>
    </Layout>
  );
};

export default Brand;
